import React from "react";
import PieChartComponent from "./PieChartComponent";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";
import inflightLogo from "../assets/inflight-logo.png";

function Dashboard() {
  return (
    <div style={{ padding: "2rem", backgroundColor: "#ffffff" }}>
      {/* Header with Logo and Navigation */}
      <div style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        marginBottom: "2rem"
      }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={inflightLogo}
            alt="In-Flight Insights Logo"
            style={{ height: "100px", marginRight: "1rem", background: "transparent" }}
          />
          <h1 style={{ fontSize: "2rem", color: "#2a5d9f" }}>
            ‚úàÔ∏è In-Flight Insights Dashboard
          </h1>
        </div>

        <div style={{ display: "flex", gap: "1.5rem" }}>
          <a href="/dashboard" style={{ color: "#2a5d9f", fontWeight: "bold" }}>Dashboard</a>
          <a href="/sop" style={{ color: "#2a5d9f", fontWeight: "bold" }}>SOP</a>
          <a href="/savings" style={{ color: "#2a5d9f", fontWeight: "bold" }}>Savings</a>
        </div>
      </div>

      {/* KPI Cards */}
      <div style={{ display: "flex", gap: "1.5rem", marginBottom: "3rem" }}>
        {[
          { title: "üí∞ Cost Savings", value: "$1.2M" },
          { title: "‚ôªÔ∏è Waste Saved", value: "210 kg" },
          { title: "üõ´ Routes Analyzed", value: "350" }
        ].map((kpi, idx) => (
          <div
            key={idx}
            style={{
              flex: 1,
              padding: "2rem",
              borderRadius: "12px",
              background: "linear-gradient(to right, #e1f5e9, #e3f2fd)",
              textAlign: "center",
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            <div style={{ fontWeight: "bold", fontSize: "1.4rem", marginBottom: "1rem" }}>
              {kpi.title}
            </div>
            <div style={{ fontSize: "2.5rem", fontWeight: "700" }}>{kpi.value}</div>
          </div>
        ))}
      </div>

      {/* Chart Section: Pie + Route Line */}
      <div style={{ display: "flex", gap: "2rem", alignItems: "flex-start", borderTop: "1px solid lightgray", paddingTop: "2rem", marginBottom: "2rem" }}>
        <div style={{ flex: 1 }}>
          <h2>Overall Food Consumption</h2>
          <PieChartComponent />
        </div>
        <div style={{ flex: 1 }}>
          <h2>Route-Specific Waste Trend</h2>
          <RouteLineChart />
        </div>
      </div>

      {/* Bar Chart */}
      <div style={{ marginBottom: "2rem" }}>
        <h2>Waste by Age Group</h2>
        <BarChartComponent />
      </div>

      {/* Age Group Details */}
      <div>
        <h2>Age Group Details</h2>
        <p>[Details will be shown here on click of bar chart]</p>
        <AgeGroupDetails />
      </div>
    </div>
  );
}

export default Dashboard;
