import React from "react";
import PieChartFood from "./PieChartFood";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import logo from "../assets/inflight-logo.png";

function Dashboard() {
  return (
    <div style={{ padding: "1rem", backgroundColor: "#fff" }}>
      {/* Logo & Title */}
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <img
          src={logo}
          alt="In-Flight Insights Logo"
          style={{ height: "60px", marginBottom: "0.5rem" }}
        />
        <h1 style={{ fontSize: "2.2rem", color: "#2c6ed5" }}>
          ‚úàÔ∏è In-Flight Insights Dashboard
        </h1>
      </div>

      {/* KPI Indicators */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          marginBottom: "1.5rem",
          gap: "1rem",
          flexWrap: "wrap"
        }}
      >
        {[
          {
            icon: "üí∞",
            title: "Cost Savings",
            value: "$1.2M"
          },
          {
            icon: "‚ôªÔ∏è",
            title: "Waste Saved",
            value: "210 kg"
          },
          {
            icon: "üõ´",
            title: "Routes Analyzed",
            value: "350"
          },
          {
            icon: "üåç",
            title: "CO‚ÇÇ Saved",
            value: "28 tonnes"
          }
        ].map((kpi, index) => (
          <div
            key={index}
            style={{
              flex: "1 1 200px",
              padding: "1rem",
              borderRadius: "12px",
              background: "linear-gradient(to right, #e0f7e9, #e3f2fd)",
              textAlign: "center",
              boxShadow: "0 2px 6px rgba(0, 0, 0, 0.08)"
            }}
          >
            <h3 style={{ fontSize: "1.2rem", marginBottom: "0.4rem" }}>
              {kpi.icon} <strong>{kpi.title}</strong>
            </h3>
            <p style={{ fontSize: "1.8rem", fontWeight: "bold", margin: 0 }}>
              {kpi.value}
            </p>
          </div>
        ))}
      </div>

      {/* Charts Container */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          flexWrap: "wrap",
          gap: "1.5rem",
          marginBottom: "2rem"
        }}
      >
        {/* Pie Charts in One Box */}
        <div
          style={{
            flex: "1 1 500px",
            backgroundColor: "#f9f9f9",
            padding: "1rem",
            borderRadius: "12px"
          }}
        >
          <h2 style={{ textAlign: "center", fontSize: "1.5rem" }}>
            Consumption Breakdown
          </h2>
          <div style={{ display: "flex", justifyContent: "space-around" }}>
            <div>
              <h3 style={{ textAlign: "center" }}>Food</h3>
              <PieChartFood />
            </div>
            <div>
              <h3 style={{ textAlign: "center" }}>Beverages</h3>
              <PieChartBeverage />
            </div>
          </div>
        </div>

        {/* Route Chart in its own box */}
        <div
          style={{
            flex: "1 1 500px",
            backgroundColor: "#f9f9f9",
            padding: "1rem",
            borderRadius: "12px"
          }}
        >
          <h2 style={{ textAlign: "center", fontSize: "1.5rem" }}>
            Route-Specific Waste Trend
          </h2>
          <RouteLineChart />
        </div>
      </div>

      {/* Bar Chart */}
      <div>
        <h2 style={{ fontSize: "1.5rem", marginBottom: "0.5rem" }}>
          Waste by Age Group
        </h2>
        <BarChartComponent />
      </div>
    </div>
  );
}

export default Dashboard;
