import React from "react";
import { Link } from "react-router-dom";
import PieChartComponent from "./PieChartComponent";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";

function Dashboard() {
  return (
    <div style={{ fontFamily: "Arial, sans-serif" }}>
      {/* üîµ Navigation Bar */}
      <nav
        style={{
          backgroundColor: "#ffffff",
          padding: "1rem 2rem",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h2 style={{ margin: 0, fontWeight: "bold", color: "#007BFF" }}>In-Flight Insights</h2>
        <div style={{ display: "flex", gap: "2rem" }}>
          <Link to="/dashboard" style={navLinkStyle}>
            Dashboard
          </Link>
          <Link to="/reports" style={navLinkStyle}>
            Reports
          </Link>
          <Link to="/savings" style={navLinkStyle}>
            Savings
          </Link>
        </div>
      </nav>

      {/* Dashboard Heading */}
      <div style={{ padding: "2rem" }}>
        <h1 style={{ textAlign: "center", fontSize: "2rem", fontWeight: "bold" }}>
          ‚úàÔ∏è In-Flight Insights Dashboard
        </h1>

        {/* üî∂ Row layout for Pie and Line charts */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            gap: "2rem",
            marginTop: "2rem",
          }}
        >
          <div style={{ flex: 1 }}>
            <h2>Overall Food Consumption</h2>
            <PieChartComponent />
          </div>

          <div style={{ flex: 1 }}>
            <h2>Route-Specific Waste Trend</h2>
            <RouteLineChart />
          </div>
        </div>

        <div style={{ marginTop: "2rem" }}>
          <h2>Waste by Age Group</h2>
          <BarChartComponent />
        </div>

        <div>
          <h2>Age Group Details</h2>
          <p>[Details will be shown here on click of bar chart]</p>
          <AgeGroupDetails />
        </div>
      </div>
    </div>
  );
}

const navLinkStyle = {
  textDecoration: "none",
  color: "#333",
  fontWeight: "500",
  fontSize: "1rem",
};

export default Dashboard;
