import React from "react";
import { useParams } from "react-router-dom";
import data from "../data/foodData.json";

const DetailPage = () => {
  const { status } = useParams();

  const filteredItems = data.filter(
    (item) => item["Consumption Status"] === status
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>Summary for: {status}</h2>
      <p>Total Items: {filteredItems.length}</p>

      <ul>
        {filteredItems.map((item, index) => (
          <li key={index}>
            <strong>{item["Food Item"]}</strong> â€” {item["Meal Type"]}, {item["Flight Route"]}, {item["Waste Weight (g)"]}g
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DetailPage;
