import React, { useState } from 'react';
import {
  LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend, ResponsiveContainer
} from 'recharts';

const wasteTrendData = {
  'SEA-JFK': [
    { quarter: 'Q1', waste: 4400 },
    { quarter: 'Q2', waste: 3900 },
    { quarter: 'Q3', waste: 3500 },
    { quarter: 'Q4', waste: 3100 }
  ],
  'ATL-PHX': [
    { quarter: 'Q1', waste: 4700 },
    { quarter: 'Q2', waste: 4300 },
    { quarter: 'Q3', waste: 4100 },
    { quarter: 'Q4', waste: 3600 }
  ],
  'LAX-SFO': [
    { quarter: 'Q1', waste: 4200 },
    { quarter: 'Q2', waste: 3900 },
    { quarter: 'Q3', waste: 3400 },
    { quarter: 'Q4', waste: 3100 }
  ]
};

const RouteLineChart = () => {
  const [selectedRoute, setSelectedRoute] = useState('SEA-JFK');

  return (
    <div style={{ backgroundColor: '#f9f9f9', padding: '1.5rem', borderRadius: '12px', marginTop: '2rem' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <h2 style={{ margin: 0 }}>Waste Trend - {selectedRoute}</h2>
        <select
          value={selectedRoute}
          onChange={(e) => setSelectedRoute(e.target.value)}
          style={{ padding: '6px 10px', borderRadius: '4px', fontSize: '1rem' }}
        >
          {Object.keys(wasteTrendData).map((route) => (
            <option key={route} value={route}>{route}</option>
          ))}
        </select>
      </div>

      <ResponsiveContainer width="100%" height={300}>
        <LineChart
          data={wasteTrendData[selectedRoute]}
          margin={{ top: 5, right: 20, left: 0, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="quarter" label={{ value: 'Quarter', position: 'insideBottom', offset: -5 }} />
          <YAxis label={{ value: 'Waste (g)', angle: -90, position: 'insideLeft', offset: 10 }} />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="waste" stroke="#8884d8" activeDot={{ r: 6 }} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default RouteLineChart;
