import React, { useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";
import data from "../data/foodData.json";

const RouteLineChart = () => {
  const [selectedRoute, setSelectedRoute] = useState(null);

  // Aggregate waste per route
  const routeData = data.reduce((acc, item) => {
    const route = item["Flight Route"];
    const weight = parseInt(item["Weight (g)"], 10);
    if (!acc[route]) {
      acc[route] = { route, totalWaste: 0 };
    }
    acc[route].totalWaste += weight;
    return acc;
  }, {});

  const chartData = Object.values(routeData);

  // When a route is clicked, filter data for detail view
  const handlePointClick = (e) => {
    setSelectedRoute(e.route);
  };

  // Get waste breakdown by food item for selected route
  const wasteByFoodItem = selectedRoute
    ? data
        .filter((item) => item["Flight Route"] === selectedRoute)
        .reduce((acc, item) => {
          const food = item["Food Item"];
          const weight = parseInt(item["Weight (g)"], 10);
          acc[food] = (acc[food] || 0) + weight;
          return acc;
        }, {})
    : {};

  return (
    <div style={{ padding: "20px" }}>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart
          data={chartData}
          margin={{ top: 10, right: 30, left: 0, bottom: 5 }}
          onClick={(e) => handlePointClick(e.activePayload[0].payload)}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="route" />
          <YAxis label={{ value: "Waste (g)", angle: -90, position: "insideLeft" }} />
          <Tooltip />
          <Line type="monotone" dataKey="totalWaste" stroke="#8884d8" activeDot={{ r: 8 }} />
        </LineChart>
      </ResponsiveContainer>

      {selectedRoute && (
        <div style={{ marginTop: "30px", background: "#f8f8f8", padding: "15px", borderRadius: "8px" }}>
          <h3>üçΩÔ∏è Waste Breakdown for {selectedRoute}</h3>
          <ul>
            {Object.entries(wasteByFoodItem).map(([food, weight]) => (
              <li key={food}>
                <strong>{food}</strong>: {weight}g
              </li>
            ))}
          </ul>
          <p style={{ marginTop: "10px", fontStyle: "italic" }}>
            ‚úàÔ∏è Insight: {selectedRoute} has significant waste from <strong>{Object.entries(wasteByFoodItem).sort((a, b) => b[1] - a[1])[0]?.[0]}</strong>. Consider reducing quantity or offering alternatives for improved sustainability.
          </p>
        </div>
      )}
    </div>
  );
};

export default RouteLineChart;
