import React from "react";
import { useParams } from "react-router-dom";
import data from "../data/foodData.json";
import jsPDF from "jspdf";

const DetailPage = () => {
  const { status } = useParams();
  const filteredItems = data.filter(item => item["Consumption Status"] === status);

  const generatePDF = () => {
    const doc = new jsPDF();

    const summary = `This report provides a breakdown of in-flight food trays marked as "${status}". There are a total of ${filteredItems.length} trays in this category. The data below includes the food item, passenger age group, flight route, and amount of waste (in grams). This can help determine patterns in consumption and waste for future meal planning.`;

    doc.setFontSize(12);
    doc.text(`Summary for: ${status}`, 14, 20);
    doc.setFontSize(10);
    doc.text(doc.splitTextToSize(summary, 180), 14, 30);

    let y = 60;

    // Table headers
    doc.text("Tray ID", 14, y);
    doc.text("Food Item", 34, y);
    doc.text("Age", 84, y);
    doc.text("Meal", 104, y);
    doc.text("Route", 134, y);
    doc.text("Waste (g)", 164, y);
    y += 8;

    // Table data
    filteredItems.forEach((item, index) => {
      if (y > 280) {
        doc.addPage();
        y = 20;
      }

      doc.text(item["Tray ID"], 14, y);
      doc.text(item["Food Item"], 34, y);
      doc.text(item["Passenger Age"], 84, y);
      doc.text(item["Meal Type"], 104, y);
      doc.text(item["Flight Route"], 134, y);
      doc.text(item["Weight (g)"].toString(), 164, y);

      y += 6;
    });

    doc.save(`${status}_Summary.pdf`);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Summary for: {status}</h2>
      <p>Total Trays: {filteredItems.length}</p>
      <button onClick={generatePDF} style={{ marginBottom: "1rem" }}>
        ðŸ“¥ Export PDF
      </button>
      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Tray ID</th>
            <th>Food Item</th>
            <th>Meal Type</th>
            <th>Flight Route</th>
            <th>Passenger Age</th>
            <th>Weight (g)</th>
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((item, idx) => (
            <tr key={idx}>
              <td>{item["Tray ID"]}</td>
              <td>{item["Food Item"]}</td>
              <td>{item["Meal Type"]}</td>
              <td>{item["Flight Route"]}</td>
              <td>{item["Passenger Age"]}</td>
              <td>{item["Weight (g)"]}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DetailPage;
