import React, { useState } from "react";
import PieChartFood from "./PieChartFood";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import inflightLogo from "../assets/inflight-logo.png";

const Dashboard = () => {
  const [selectedRoute, setSelectedRoute] = useState("NYC-LAX");
  const [showFilteredCharts, setShowFilteredCharts] = useState(false);

  const handleFilterClick = () => {
    setShowFilteredCharts(true);
  };

  return (
    <div
      style={{
        padding: "2rem",
        fontFamily: "Arial, sans-serif",
        background: "linear-gradient(to right, #c6f0d3, #d9f1fb)",
      }}
    >
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={inflightLogo}
            alt="In-Flight Insights Logo"
            style={{ height: "55px", marginRight: "10px" }}
          />
          <h1 style={{ fontSize: "1.8rem", color: "#1e90ff", margin: 0 }}>
            In-Flight Insights Dashboard
          </h1>
        </div>
        <div style={{ display: "flex", gap: "1.5rem", alignItems: "center" }}>
          <a href="#dashboard" style={{ color: "#1e90ff", fontWeight: "bold" }}>
            Dashboard
          </a>
          <a href="#reports" style={{ color: "#1e90ff", fontWeight: "bold" }}>
            Reports
          </a>
          <a href="#settings" style={{ color: "#1e90ff", fontWeight: "bold" }}>
            Settings
          </a>
          <a href="#profile" style={{ color: "#1e90ff", fontWeight: "bold" }}>
            Profile
          </a>
        </div>
      </div>

      {/* KPI Section */}
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "2rem", gap: "1rem" }}>
        {[
          { title: "💰 Cost Savings", value: "$1.2M" },
          { title: "♻️ Waste Saved", value: "210 kg" },
          { title: "✈️ Routes Analyzed", value: "350" },
          { title: "🌍 CO₂ Saved", value: "28 tonnes" },
        ].map((kpi, index) => (
          <div
            key={index}
            style={{
              background: "linear-gradient(to right, #e0ffe0, #e0f7ff)",
              padding: "1rem",
              borderRadius: "12px",
              flex: 1,
              textAlign: "center",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
            }}
          >
            <h3 style={{ marginBottom: "0.5rem" }}>{kpi.title}</h3>
            <p style={{ fontSize: "1.5rem", fontWeight: "bold" }}>{kpi.value}</p>
          </div>
        ))}
      </div>

      {/* Route Filter Buttons */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginTop: "1.5rem",
          gap: "1rem",
        }}
      >
        <select
          value={selectedRoute}
          onChange={(e) => setSelectedRoute(e.target.value)}
          style={{
            padding: "0.5rem",
            borderRadius: "6px",
            fontSize: "1rem",
          }}
        >
          <option value="NYC-LAX">NYC-LAX</option>
          <option value="ATL-PHX">ATL-PHX</option>
          <option value="SEA-JFK">SEA-JFK</option>
        </select>
        <button
          onClick={handleFilterClick}
          style={{
            backgroundColor: "#1e90ff",
            color: "white",
            border: "none",
            borderRadius: "6px",
            padding: "0.5rem 1rem",
            cursor: "pointer",
          }}
        >
          Filter by Flight Route
        </button>
      </div>

      {/* Pie Charts and Route Line Chart */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginTop: "2rem",
          gap: "2rem",
        }}
      >
        {/* Fixed-Height Pie Chart Section */}
        <div
          style={{
            backgroundColor: "#f8f9fa",
            borderRadius: "10px",
            padding: "1rem",
            flex: 1,
            minHeight: "350px",
            maxHeight: "350px",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
          }}
        >
          <h3 style={{ textAlign: "center", marginBottom: "1rem" }}>
            Consumption Breakdown
          </h3>
          <div
            style={{
              display: "flex",
              justifyContent: "space-evenly",
              alignItems: "center",
              gap: "3rem",
            }}
          >
            <div style={{ textAlign: "center" }}>
              <h4>Food</h4>
              <PieChartFood />
            </div>
            <div style={{ textAlign: "center" }}>
              <h4>Beverages</h4>
              <PieChartBeverage />
            </div>
          </div>
        </div>

        {/* Route Specific Waste Trend */}
        <div
          style={{
            backgroundColor: "#f8f9fa",
            borderRadius: "10px",
            padding: "1rem",
            flex: 1,
          }}
        >
          <RouteLineChart selectedRoute={selectedRoute} />
        </div>
      </div>

      {/* Age Group Waste */}
      <div style={{ marginTop: "2rem" }}>
        <BarChartComponent />
      </div>
    </div>
  );
};

export default Dashboard;
