import React, { useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid
} from "recharts";

const routeData = [
  { route: "SEA-JFK", waste: 3600 },
  { route: "ATL-PHX", waste: 3900 },
  { route: "LAX-SFO", waste: 3200 },
  { route: "MIA-ORD", waste: 4100 },
  { route: "NYC-LAX", waste: 3000 },
  { route: "LAS-DFW", waste: 4500 },
  { route: "BOS-DEN", waste: 4800 }
];

const routeBreakdowns = {
  "SEA-JFK": {
    "Cheese Omelette": 354,
    "Egg Roll": 552,
    "Rice & Beans": 741,
    "Burger": 452,
    "Lentil Curry": 570,
    "Salad Bowl": 341
  },
  "ATL-PHX": {
    "Burger": 658,
    "Lentil Curry": 705,
    "Cheese Omelette": 498,
    "Chicken Sandwich": 841
  },
  "LAX-SFO": {
    "Pasta Salad": 825,
    "Cheese Omelette": 546,
    "Egg Roll": 595,
    "Fruit Bowl": 884,
    "Veg Wrap": 284,
    "Salad Bowl": 650
  },
  "MIA-ORD": {
    "Veg Wrap": 252,
    "Tofu Stir Fry": 856,
    "Chicken Curry": 647,
    "Paneer Wrap": 389,
    "Cheese Omelette": 410
  },
  "NYC-LAX": {
    "Cheese Omelette": 438,
    "Chicken Curry": 544,
    "Salad Bowl": 874,
    "Rice & Beans": 744
  },
  "LAS-DFW": {
    "Paneer Wrap": 847,
    "Chicken Sandwich": 589,
    "Rice Bowl": 509,
    "Tofu Stir Fry": 292,
    "Burger": 566,
    "Chickpea Stew": 875
  },
  "BOS-DEN": {
    "Chicken Curry": 578,
    "Veg Wrap": 797,
    "Burger": 580,
    "Salad Bowl": 692,
    "Egg Roll": 667
  }
};

const RouteLineChart = () => {
  const [selectedRoute, setSelectedRoute] = useState(null);

  const handleClick = (data) => {
    setSelectedRoute(data.route);
  };

  const renderBreakdown = (route) => {
    const items = routeBreakdowns[route];
    if (!items) return null;

    const maxItem = Object.entries(items).reduce((a, b) =>
      b[1] > a[1] ? b : a
    );
    return (
      <div style={{ background: "#fff", padding: "1rem", marginTop: "1rem", borderRadius: "8px" }}>
        <h3>üóÇÔ∏è Waste Breakdown for {route}</h3>
        <ul>
          {Object.entries(items).map(([item, grams]) => (
            <li key={item}>
              <strong style={{ fontWeight: item === maxItem[0] ? "bold" : "normal" }}>
                {item}
              </strong>
              : {grams}g
            </li>
          ))}
        </ul>
        <p style={{ fontStyle: "italic", marginTop: "0.5rem" }}>
          ‚úàÔ∏è <strong>Insight:</strong> {route} has significant waste from{" "}
          <strong>{maxItem[0]}</strong>. Consider reducing quantity or offering alternatives for improved sustainability.
        </p>
      </div>
    );
  };

  return (
    <div style={{ marginTop: "1rem" }}>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={routeData} onClick={({ activePayload }) => handleClick(activePayload[0].payload)}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="route" />
          <YAxis label={{ value: "Waste (g)", angle: -90, position: "insideLeft" }} />
          <Tooltip formatter={(value) => [`${value}g`, "Waste"]} />
          <Line type="monotone" dataKey="waste" stroke="#8884d8" strokeWidth={2} dot={{ r: 5 }} />
        </LineChart>
      </ResponsiveContainer>
      {selectedRoute && renderBreakdown(selectedRoute)}
    </div>
  );
};

export default RouteLineChart;
