import React, { useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer
} from "recharts";

const sampleData = [
  { route: "BOS-DEN", waste: 4800 },
  { route: "SEA-JFK", waste: 3900 },
  { route: "ATL-PHX", waste: 4000 },
  { route: "LAX-SFO", waste: 2700 },
  { route: "LAS-DFW", waste: 4600 },
  { route: "MIA-ORD", waste: 4650 },
  { route: "NYC-LAX", waste: 3100 }
];

const routeBreakdowns = {
  "SEA-JFK": {
    "Cheese Omelette": 874,
    "Beef Burrito": 301,
    "Fruit Bowl": 650,
    "Veg Wrap": 528,
    "Pasta Salad": 869,
    "Tofu Stir Fry": 537,
    "Chicken Sandwich": 113
  },
  "ATL-PHX": {
    "Chicken Curry": 490,
    "Paneer Wrap": 330,
    "Fruit Bowl": 620,
    "Tofu Stir Fry": 512
  },
  "LAX-SFO": {
    "Burger": 700,
    "Salad": 520,
    "Pasta": 650,
    "Rice Bowl": 400
  },
  // Add other routes if needed...
};

const RouteLineChart = () => {
  const [selectedRoute, setSelectedRoute] = useState(null);

  const handleClick = (data) => {
    setSelectedRoute(data.route);
  };

  const foodBreakdown = selectedRoute ? routeBreakdowns[selectedRoute] : null;

  return (
    <div style={{ marginTop: "2rem" }}>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart
          data={sampleData}
          margin={{ top: 10, right: 40, left: 0, bottom: 10 }}
          onClick={(e) => {
            if (e && e.activePayload) handleClick(e.activePayload[0].payload);
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="route" />
          <YAxis label={{ value: "Waste (g)", angle: -90, position: "insideLeft" }} />
          <Tooltip />
          <Line
            type="monotone"
            dataKey="waste"
            stroke="#8884d8"
            strokeWidth={2}
            dot={{ r: 5 }}
            activeDot={{ r: 8 }}
          />
        </LineChart>
      </ResponsiveContainer>

      {selectedRoute && foodBreakdown && (
        <div
          style={{
            background: "#fff",
            padding: "1.5rem",
            borderRadius: "10px",
            marginTop: "2rem",
            boxShadow: "0 4px 10px rgba(0,0,0,0.1)"
          }}
        >
          <h3>üóÇÔ∏è Waste Breakdown for {selectedRoute}</h3>
          <ul style={{ lineHeight: "1.8", listStyleType: "none", padding: 0 }}>
            {(() => {
              const maxWaste = Math.max(...Object.values(foodBreakdown));
              return Object.entries(foodBreakdown).map(([food, weight]) => (
                <li key={food}>
                  {weight === maxWaste ? (
                    <strong>‚Ä¢ {food}: {weight}g</strong>
                  ) : (
                    <>‚Ä¢ {food}: {weight}g</>
                  )}
                </li>
              ));
            })()}
          </ul>
          <p style={{ marginTop: "1rem", fontStyle: "italic" }}>
            ‚úàÔ∏è <strong>Insight:</strong> {selectedRoute} has significant waste from{" "}
            <strong>
              {
                Object.entries(foodBreakdown).reduce((a, b) =>
                  a[1] > b[1] ? a : b
                )[0]
              }
            </strong>
            . Consider reducing quantity or offering alternatives for improved sustainability.
          </p>
        </div>
      )}
    </div>
  );
};

export default RouteLineChart;
