import React, { useState } from "react";
import PieChartComponent from "./PieChartComponent";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";
import logo from "../assets/inflight-logo.png";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

const flightData = {
  "SEA-JFK": [
    { quarter: "Q1", waste: 4200, savings: 2500 },
    { quarter: "Q2", waste: 3900, savings: 3000 },
    { quarter: "Q3", waste: 3600, savings: 3400 },
    { quarter: "Q4", waste: 3200, savings: 4000 },
  ],
  "NYC-LAX": [
    { quarter: "Q1", waste: 5000, savings: 2200 },
    { quarter: "Q2", waste: 4600, savings: 2800 },
    { quarter: "Q3", waste: 4200, savings: 3500 },
    { quarter: "Q4", waste: 3900, savings: 4100 },
  ],
};

function Dashboard() {
  const [searchInput, setSearchInput] = useState("");
  const [selectedFlightData, setSelectedFlightData] = useState(null);

  const handleSearch = () => {
    if (flightData[searchInput]) {
      setSelectedFlightData(flightData[searchInput]);
    } else {
      setSelectedFlightData(null);
    }
  };

  return (
    <div style={{ padding: "1.5rem", backgroundColor: "#f4fcff" }}>
      {/* Header and Nav */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1.5rem" }}>
        <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
          <img src={logo} alt="In-Flight Insights Logo" style={{ height: "55px" }} />
          <h1 style={{ fontSize: "1.8rem", color: "#2575fc" }}>✈️ In-Flight Insights Dashboard</h1>
        </div>

        {/* Nav + Search */}
        <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
          <input
            type="text"
            placeholder="Search Flight (e.g., SEA-JFK)"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            style={{
              padding: "0.4rem 0.8rem",
              fontSize: "0.9rem",
              borderRadius: "5px",
              border: "1px solid #ccc",
              width: "220px",
            }}
          />
          <button
            onClick={handleSearch}
            style={{
              backgroundColor: "#2575fc",
              color: "white",
              padding: "0.4rem 1rem",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Search
          </button>
          <span style={{ color: "#2575fc", fontWeight: "bold" }}>Dashboard</span>
          <span style={{ color: "#2575fc", fontWeight: "bold" }}>Reports</span>
          <span style={{ color: "#2575fc", fontWeight: "bold" }}>Settings</span>
          <span style={{ color: "#2575fc", fontWeight: "bold" }}>Profile</span>
        </div>
      </div>

      {/* KPI Cards */}
      <div style={{ display: "flex", justifyContent: "space-between", gap: "1rem", marginBottom: "2rem" }}>
        {[
          { icon: "💰", title: "Cost Savings", value: "$1.2M" },
          { icon: "♻️", title: "Waste Saved", value: "210 kg" },
          { icon: "🛫", title: "Routes Analyzed", value: "350" },
          { icon: "🌍", title: "CO₂ Saved", value: "28 tonnes" },
        ].map((kpi, i) => (
          <div
            key={i}
            style={{
              flex: 1,
              background: "linear-gradient(to right, #e0f9dc, #e0f3ff)",
              borderRadius: "10px",
              padding: "1rem",
              textAlign: "center",
              boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
            }}
          >
            <h3 style={{ fontSize: "1.2rem", marginBottom: "0.3rem" }}>{kpi.icon} {kpi.title}</h3>
            <p style={{ fontSize: "1.8rem", fontWeight: "bold", margin: 0 }}>{kpi.value}</p>
          </div>
        ))}
      </div>

      {/* 🚀 Flight Route Graph (inline below KPIs) */}
      {selectedFlightData && (
        <div style={{ background: "#ffffff", padding: "1.5rem", borderRadius: "10px", marginBottom: "2rem" }}>
          <h3 style={{ marginBottom: "1rem", color: "#2575fc" }}>
            Quarterly Waste & Savings for <strong>{searchInput}</strong>
          </h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={selectedFlightData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="quarter" label={{ value: "Quarter", position: "insideBottom", offset: -5 }} />
              <YAxis label={{ value: "Grams / USD", angle: -90, position: "insideLeft" }} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="waste" stroke="#ff6f61" name="Waste (g)" />
              <Line type="monotone" dataKey="savings" stroke="#2e8b57" name="Savings ($)" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}

      {/* Pie Charts and Line Chart */}
      <div style={{ display: "flex", gap: "1rem", marginBottom: "2rem" }}>
        <div style={{ flex: 1, background: "#f8f8f8", borderRadius: "10px", padding: "1rem" }}>
          <h3 style={{ textAlign: "center" }}>Consumption Breakdown</h3>
          <div style={{ display: "flex", justifyContent: "space-around" }}>
            <div>
              <h4 style={{ textAlign: "center" }}>Food</h4>
              <PieChartComponent />
            </div>
            <div>
              <h4 style={{ textAlign: "center" }}>Beverages</h4>
              <PieChartBeverage />
            </div>
          </div>
        </div>
        <div style={{ flex: 1, background: "#f8f8f8", borderRadius: "10px", padding: "1rem" }}>
          <h3 style={{ textAlign: "center" }}>Waste Trend</h3>
          <RouteLineChart />
        </div>
      </div>

      {/* Bar Chart + Age Group Details */}
      <div style={{ marginBottom: "2rem" }}>
        <h3>Waste by Age Group</h3>
        <BarChartComponent />
      </div>

      <div style={{ marginBottom: "2rem" }}>
        <h3>Age Group Details</h3>
        <AgeGroupDetails />
      </div>
    </div>
  );
}

export default Dashboard;
