// RouteLineChart.jsx

import React, { useState } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

const flightData = {
  "SEA-JFK": [
    { quarter: "Q1", waste: 4200 },
    { quarter: "Q2", waste: 3900 },
    { quarter: "Q3", waste: 3600 },
    { quarter: "Q4", waste: 3300 }
  ],
  "ATL-PHX": [
    { quarter: "Q1", waste: 4000 },
    { quarter: "Q2", waste: 3700 },
    { quarter: "Q3", waste: 3500 },
    { quarter: "Q4", waste: 3200 }
  ],
  "NYC-LAX": [
    { quarter: "Q1", waste: 4300 },
    { quarter: "Q2", waste: 4000 },
    { quarter: "Q3", waste: 3700 },
    { quarter: "Q4", waste: 3400 }
  ]
};

const RouteLineChart = () => {
  const [selectedRoute, setSelectedRoute] = useState("SEA-JFK");

  const handleRouteChange = (event) => {
    setSelectedRoute(event.target.value);
  };

  return (
    <div style={{ background: "#f9f9f9", padding: "1rem", borderRadius: "12px", marginTop: "1.5rem" }}>
      <h2 style={{ textAlign: "center", marginBottom: "1rem" }}>Waste Trend - {selectedRoute}</h2>
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <select value={selectedRoute} onChange={handleRouteChange} style={{ padding: "0.5rem", fontSize: "1rem" }}>
          {Object.keys(flightData).map((route) => (
            <option key={route} value={route}>{route}</option>
          ))}
        </select>
      </div>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={flightData[selectedRoute]}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="quarter" label={{ value: "Quarter", position: "insideBottom", offset: -5 }} />
          <YAxis label={{ value: "Waste (g)", angle: -90, position: "insideLeft" }} />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="waste" stroke="#8884d8" name="Waste (g)" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default RouteLineChart;
