import React, { useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer
} from "recharts";

const wasteData = {
  "SEA-JFK": [
    { quarter: "Q1", waste: 4500 },
    { quarter: "Q2", waste: 3900 },
    { quarter: "Q3", waste: 3400 },
    { quarter: "Q4", waste: 3100 }
  ],
  "ATL-PHX": [
    { quarter: "Q1", waste: 4200 },
    { quarter: "Q2", waste: 4100 },
    { quarter: "Q3", waste: 3800 },
    { quarter: "Q4", waste: 3500 }
  ]
};

const RouteLineChart = () => {
  const [selectedRoute, setSelectedRoute] = useState("SEA-JFK");

  const handleChange = (e) => setSelectedRoute(e.target.value);

  return (
    <div>
      <h3 style={{ textAlign: "center" }}>Waste Trend - {selectedRoute}</h3>
      <div style={{ display: "flex", justifyContent: "flex-end", marginBottom: "10px" }}>
        <select
          value={selectedRoute}
          onChange={handleChange}
          style={{ padding: "5px", borderRadius: "5px" }}
        >
          {Object.keys(wasteData).map((route) => (
            <option key={route} value={route}>
              {route}
            </option>
          ))}
        </select>
      </div>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart
          data={wasteData[selectedRoute]}
          margin={{ top: 20, right: 30, left: 20, bottom: 50 }} // more bottom space
        >
          <XAxis dataKey="quarter" label={{ value: "Quarter", position: "insideBottom", offset: -10 }} />
          <YAxis label={{ value: "Waste (g)", angle: -90, position: "insideLeft" }} />
          <Tooltip />
          <Legend verticalAlign="top" height={36} wrapperStyle={{ bottom: -30 }} /> {/* pushed below */}
          <Line
            type="monotone"
            dataKey="waste"
            stroke="#8884d8"
            strokeWidth={2}
            dot={{ r: 4 }}
            activeDot={{ r: 8 }}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default RouteLineChart;
