import React from "react";
import { useParams } from "react-router-dom";
import data from "../data/foodData.json";
import jsPDF from "jspdf";

const AgeGroupDetails = () => {
  const { age } = useParams();
  const filtered = data.filter(item => item["Passenger Age"] === age);

  const totalWaste = filtered.reduce((sum, item) => sum + item["Weight (g)"], 0);
  const avgWaste = (totalWaste / filtered.length).toFixed(2);

  const generatePDF = () => {
    const doc = new jsPDF();
    const summary = `
This report details waste patterns for passengers in the "${age}" age group. A total of ${filtered.length} trays are recorded, contributing ${totalWaste} grams of food waste â€” averaging ${avgWaste}g per tray.

This data helps identify which meals result in more waste from this demographic, enabling sustainability officers to adjust meal planning and reduce environmental impact.
    `;

    doc.setFontSize(12);
    doc.text(`Age Group Summary: ${age}`, 14, 20);
    doc.setFontSize(10);
    doc.text(doc.splitTextToSize(summary, 180), 14, 30);

    let y = 70;
    doc.text("Tray ID", 14, y);
    doc.text("Food", 34, y);
    doc.text("Route", 84, y);
    doc.text("Waste (g)", 134, y);
    y += 8;

    filtered.forEach(item => {
      if (y > 280) {
        doc.addPage();
        y = 20;
      }
      doc.text(item["Tray ID"], 14, y);
      doc.text(item["Food Item"], 34, y);
      doc.text(item["Flight Route"], 84, y);
      doc.text(item["Weight (g)"].toString(), 134, y);
      y += 6;
    });

    doc.save(`AgeGroup_${age}_Summary.pdf`);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“‹ Waste Details for Age Group: {age}</h2>
      <p>Total Trays: {filtered.length}</p>
      <p>Total Waste: {totalWaste}g | Average Waste: {avgWaste}g</p>
      <button onClick={generatePDF} style={{ marginBottom: "1rem" }}>
        ðŸ“¥ Export PDF
      </button>

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Tray ID</th>
            <th>Food Item</th>
            <th>Flight Route</th>
            <th>Waste (g)</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((item, index) => (
            <tr key={index}>
              <td>{item["Tray ID"]}</td>
              <td>{item["Food Item"]}</td>
              <td>{item["Flight Route"]}</td>
              <td>{item["Weight (g)"]}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AgeGroupDetails;
