import React from "react";
import PieChartComponent from "./PieChartComponent";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";
import { Link } from "react-router-dom";

function Dashboard() {
  return (
    <div
      style={{
        backgroundColor: "#ffffff",
        minHeight: "100vh",
        padding: "1rem 2rem",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
      }}
    >
      {/* Navigation */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "2rem"
        }}
      >
        <h1 style={{ color: "#004e92" }}>‚úàÔ∏è In-Flight Insights Dashboard</h1>
        <nav style={{ display: "flex", gap: "1.5rem" }}>
          <Link to="/dashboard" style={{ color: "#004e92", textDecoration: "none", fontWeight: 600 }}>
            Dashboard
          </Link>
          <Link to="/sop" style={{ color: "#004e92", textDecoration: "none", fontWeight: 600 }}>
            Reports
          </Link>
          <a href="#kpi" style={{ color: "#004e92", textDecoration: "none", fontWeight: 600 }}>
            Savings
          </a>
        </nav>
      </div>

      {/* KPI Section */}
      <section id="kpi" style={{ display: "flex", gap: "1.5rem", marginBottom: "2rem" }}>
        {[
          {
            title: "üí∞ Cost Savings",
            value: "$1.2M"
          },
          {
            title: "‚ôªÔ∏è Waste Saved",
            value: "210 kg"
          },
          {
            title: "üõ¨ Routes Analyzed",
            value: "350"
          }
        ].map((kpi, idx) => (
          <div
            key={idx}
            style={{
              background: "linear-gradient(to bottom, #c9f0ff, #d4fcdc)",
              padding: "1.2rem",
              flex: 1,
              borderRadius: "10px",
              boxShadow: "0px 2px 6px rgba(0,0,0,0.1)",
              textAlign: "center"
            }}
          >
            <h3 style={{ margin: "0 0 10px", color: "#444" }}>{kpi.title}</h3>
            <p style={{ fontSize: "1.8rem", fontWeight: "bold", margin: 0 }}>{kpi.value}</p>
          </div>
        ))}
      </section>

      {/* Food and Route Charts - Side by side */}
      <div style={{ display: "flex", gap: "2rem", marginBottom: "3rem" }}>
        <div style={{ flex: 1 }}>
          <h2>Overall Food Consumption</h2>
          <PieChartComponent />
        </div>
        <div style={{ flex: 1 }}>
          <h2>Route-Specific Waste Trend</h2>
          <RouteLineChart />
        </div>
      </div>

      {/* Bar Chart and Age Group Details */}
      <div style={{ marginBottom: "2rem" }}>
        <h2>Waste by Age Group</h2>
        <BarChartComponent />
      </div>

      <div>
        <h2>Age Group Details</h2>
        <p>[Details will be shown here on click of bar chart]</p>
        <AgeGroupDetails />
      </div>
    </div>
  );
}

export default Dashboard;
