import React, { useState } from "react";
import logo from "../assets/inflight-logo.png";
import PieChartFood from "./PieChartFood";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";
import DetailPage from "./DetailPage";
import RouteInsightBox from "./RouteInsightBox";
import RouteFilter from "./RouteFilter";

const Dashboard = () => {
  const [selectedStatus, setSelectedStatus] = useState(null);
  const [selectedAgeGroup, setSelectedAgeGroup] = useState(null);
  const [selectedRoute, setSelectedRoute] = useState("");
  const [searchInput, setSearchInput] = useState("");
  const [showRouteChart, setShowRouteChart] = useState(false);

  const handleBack = () => {
    setSelectedRoute("");
    setShowRouteChart(false);
  };

  const handleSearch = () => {
    if (searchInput.trim() !== "") {
      setSelectedRoute(searchInput.trim().toUpperCase());
      setShowRouteChart(true);
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", background: "linear-gradient(to bottom right, #e0f7fa, #ccffcc)", minHeight: "100vh" }}>
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", padding: "1rem 2rem", alignItems: "center" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="In-Flight Insights Logo" style={{ width: 50, height: 50, marginRight: "0.75rem" }} />
          <h1 style={{ color: "#1976d2", fontSize: "1.8rem", margin: 0 }}>✈️ In-Flight Insights Dashboard</h1>
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: "0.75rem" }}>
          <input
            type="text"
            placeholder="Enter Flight Route"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            style={{ padding: "5px", fontSize: "0.9rem", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <button onClick={handleSearch} style={{ backgroundColor: "#1976d2", color: "white", border: "none", padding: "6px 12px", borderRadius: "5px" }}>
            Search
          </button>
          <div style={{ display: "flex", gap: "1.5rem", marginLeft: "1.5rem" }}>
            <span style={{ cursor: "pointer", color: "#1976d2", fontWeight: "bold" }}>Dashboard</span>
            <span style={{ cursor: "pointer", color: "#1976d2", fontWeight: "bold" }}>Reports</span>
            <span style={{ cursor: "pointer", color: "#1976d2", fontWeight: "bold" }}>Settings</span>
            <span style={{ cursor: "pointer", color: "#1976d2", fontWeight: "bold" }}>Profile</span>
          </div>
        </div>
      </div>

      {/* KPI Section */}
      <div style={{ display: "flex", justifyContent: "space-around", padding: "1rem 2rem", marginTop: "-0.5rem" }}>
        {[
          { label: "💰 Cost Savings", value: "$1.2M" },
          { label: "♻️ Waste Saved", value: "210 kg" },
          { label: "✈️ Routes Analyzed", value: "350" },
          { label: "🌍 CO₂ Saved", value: "28 tonnes" }
        ].map((kpi, index) => (
          <div key={index} style={{ flex: 1, margin: "0 1rem", background: "linear-gradient(to right, #e0f7fa, #f1fff0)", padding: "1.2rem", borderRadius: "12px", textAlign: "center", boxShadow: "0 2px 8px rgba(0,0,0,0.08)" }}>
            <h3 style={{ fontSize: "1.1rem", margin: "0.3rem 0", color: "#333" }}>{kpi.label}</h3>
            <p style={{ fontSize: "1.5rem", fontWeight: "bold", margin: 0 }}>{kpi.value}</p>
          </div>
        ))}
      </div>

      {/* Conditional Route Chart Display with Back Button */}
      {showRouteChart && selectedRoute && (
        <div style={{ margin: "1rem 2rem", position: "relative" }}>
          <RouteFilter route={selectedRoute} />
          <button
            onClick={handleBack}
            style={{
              position: "absolute",
              top: 10,
              right: 30,
              background: "#f44336",
              color: "white",
              border: "none",
              padding: "6px 12px",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            Back
          </button>
        </div>
      )}

      {/* Pie + Route Chart */}
      <div style={{ display: "flex", justifyContent: "space-between", padding: "2rem" }}>
        <div style={{ flex: 1, marginRight: "1.5rem", backgroundColor: "#f9f9f9", padding: "1rem", borderRadius: "10px" }}>
          <h3 style={{ textAlign: "center" }}>Consumption Breakdown</h3>
          <div style={{ display: "flex", justifyContent: "space-around" }}>
            <PieChartFood />
            <PieChartBeverage />
          </div>
        </div>
        <div style={{ flex: 1.2, backgroundColor: "#f9f9f9", padding: "1rem", borderRadius: "10px" }}>
          <RouteLineChart setSelectedRoute={setSelectedRoute} />
        </div>
      </div>

      {/* Age Breakdown */}
      <div style={{ padding: "0 2rem" }}>
        <BarChartComponent setSelectedAgeGroup={setSelectedAgeGroup} />
        <AgeGroupDetails selectedAgeGroup={selectedAgeGroup} />
      </div>

      {/* Detail Page for Pie Slice */}
      {selectedStatus && (
        <div style={{ padding: "2rem" }}>
          <DetailPage status={selectedStatus} />
        </div>
      )}

      {/* Route Detail Breakdown from RouteLineChart click */}
      <RouteInsightBox selectedRoute={selectedRoute} />
    </div>
  );
};

export default Dashboard;
