import React, { useState } from "react";
import PieChartFood from "./PieChartFood";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";
import logo from "../assets/inflight-logo.png";

function Dashboard() {
  const [selectedRoute, setSelectedRoute] = useState("NYC-LAX");

  return (
    <div style={{ padding: "1rem", fontFamily: "Arial, sans-serif", background: "linear-gradient(to bottom right, #e6faff, #d6f5e7)" }}>
      {/* Navigation */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1.5rem" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="In-Flight Insights Logo" style={{ width: "60px", marginRight: "10px" }} />
          <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", color: "#2a6edc" }}>✈️ In-Flight Insights Dashboard</h1>
        </div>
        <div style={{ display: "flex", gap: "20px" }}>
          <a href="#" style={{ color: "#2a6edc", fontWeight: "bold", textDecoration: "none" }}>Dashboard</a>
          <a href="#" style={{ color: "#2a6edc", fontWeight: "bold", textDecoration: "none" }}>Reports</a>
          <a href="#" style={{ color: "#2a6edc", fontWeight: "bold", textDecoration: "none" }}>Settings</a>
          <a href="#" style={{ color: "#2a6edc", fontWeight: "bold", textDecoration: "none" }}>Profile</a>
        </div>
      </div>

      {/* KPI Cards */}
      <div style={{ display: "flex", justifyContent: "space-between", gap: "1rem", marginBottom: "1rem" }}>
        {[
          { label: "💰 Cost Savings", value: "$1.2M" },
          { label: "♻️ Waste Saved", value: "210 kg" },
          { label: "✈️ Routes Analyzed", value: "350" },
          { label: "🌍 CO₂ Saved", value: "28 tonnes" },
        ].map((kpi, i) => (
          <div
            key={i}
            style={{
              flex: 1,
              background: "linear-gradient(to right, #e1f8dc, #e6f6fd)",
              padding: "1rem",
              borderRadius: "10px",
              textAlign: "center",
              boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            }}
          >
            <h3 style={{ fontSize: "1.1rem", marginBottom: "0.5rem" }}>{kpi.label}</h3>
            <p style={{ fontSize: "1.6rem", fontWeight: "bold", margin: 0 }}>{kpi.value}</p>
          </div>
        ))}
      </div>

      {/* Flight Route Filter - Centralized */}
      <div style={{ display: "flex", justifyContent: "center", gap: "1rem", margin: "1rem 0" }}>
        <select
          value={selectedRoute}
          onChange={(e) => setSelectedRoute(e.target.value)}
          style={{
            padding: "0.5rem",
            fontSize: "1rem",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        >
          {["NYC-LAX", "SEA-JFK", "ATL-PHX", "LAX-SFO", "MIA-ORD", "LAS-DFW", "BOS-DEN"].map((route) => (
            <option key={route} value={route}>{route}</option>
          ))}
        </select>
        <button
          style={{
            backgroundColor: "#2a6edc",
            color: "#fff",
            padding: "0.5rem 1rem",
            fontSize: "1rem",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Filter by Flight Route
        </button>
      </div>

      {/* Consumption Breakdown & Waste Trend */}
      <div style={{ display: "flex", gap: "1rem", marginBottom: "1rem" }}>
        <div style={{ flex: 1, background: "#f9f9f9", borderRadius: "10px", padding: "1rem" }}>
          <h3 style={{ textAlign: "center" }}>Consumption Breakdown</h3>
          <div style={{ display: "flex", justifyContent: "space-around" }}>
            <div>
              <h4 style={{ textAlign: "center" }}>Food</h4>
              <PieChartFood />
            </div>
            <div>
              <h4 style={{ textAlign: "center" }}>Beverages</h4>
              <PieChartBeverage />
            </div>
          </div>
        </div>

        <div style={{ flex: 1, background: "#f9f9f9", borderRadius: "10px", padding: "1rem" }}>
          <h3 style={{ textAlign: "center" }}>Waste Trend</h3>
          <RouteLineChart selectedRoute={selectedRoute} />
        </div>
      </div>

      {/* Age Group Waste */}
      <div style={{ marginTop: "2rem" }}>
        <h3>Waste by Age Group</h3>
        <BarChartComponent />
      </div>

      {/* Age Group Details */}
      <div style={{ marginTop: "1rem" }}>
        <h3>Age Group Details</h3>
        <p>[Details will be shown here on click of bar chart]</p>
        <AgeGroupDetails />
      </div>
    </div>
  );
}

export default Dashboard;
