import React from "react";
import { PieChart, Pie, Cell, Tooltip } from "recharts";
import { useNavigate } from "react-router-dom";
import data from "../data/foodData.json";

const PieChartComponent = () => {
  const navigate = useNavigate();

  // Group data by "Consumption Status"
  const grouped = data.reduce((acc, item) => {
    const status = item["Consumption Status"];
    acc[status] = (acc[status] || 0) + 1;
    return acc;
  }, {});

  const pieData = Object.entries(grouped).map(([name, value]) => ({
    name,
    value,
  }));

  const COLORS = ["#8884d8", "#82ca9d", "#ffc658"]; // Purple, Green, Yellow

  const handleClick = (entry) => {
    navigate(`/details/${entry.name}`);
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", margin: "20px" }}>
      <PieChart width={400} height={300}>
        <Pie
          data={pieData}
          cx="50%"
          cy="50%"
          outerRadius={100}
          label
          dataKey="value"
          onClick={handleClick}
        >
          {pieData.map((_, index) => (
            <Cell
              key={`cell-${index}`}
              fill={COLORS[index % COLORS.length]}
              cursor="pointer"
            />
          ))}
        </Pie>
        <Tooltip />
      </PieChart>
    </div>
  );
};

export default PieChartComponent;
