import React, { useState } from "react";
import PieChartFood from "./PieChartFood";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";
import logo from "../assets/inflight-logo.png";
import RouteInsightBox from "./RouteInsightBox";
import RouteFilter from "./RouteFilter";

const Dashboard = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedRoute, setSelectedRoute] = useState("");
  const [showInsightChart, setShowInsightChart] = useState(false);

  const handleSearch = () => {
    if (searchTerm) {
      setSelectedRoute(searchTerm);
      setShowInsightChart(true);
    }
  };

  const handleClearChart = () => {
    setShowInsightChart(false);
    setSelectedRoute("");
  };

  return (
    <div style={{ padding: "1rem", fontFamily: "Arial, sans-serif", background: "linear-gradient(to right, #e0f7fa, #e0fce5)" }}>
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="In-Flight Insights Logo" style={{ height: "55px", marginRight: "10px" }} />
          <h1 style={{ color: "#1976d2", fontSize: "1.8rem", margin: 0 }}>‚úàÔ∏è In-Flight Insights Dashboard</h1>
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
          <input
            type="text"
            placeholder="Enter Flight Route"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ padding: "0.5rem", fontSize: "1rem", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <button
            onClick={handleSearch}
            style={{ padding: "0.5rem 1rem", backgroundColor: "#1976d2", color: "#fff", border: "none", borderRadius: "5px" }}
          >
            Search
          </button>
          <div style={{ display: "flex", gap: "1.5rem", marginLeft: "1rem" }}>
            <span style={{ color: "#1976d2", cursor: "pointer", fontWeight: "bold" }}>Dashboard</span>
            <span style={{ color: "#1976d2", cursor: "pointer", fontWeight: "bold" }}>Reports</span>
            <span style={{ color: "#1976d2", cursor: "pointer", fontWeight: "bold" }}>Settings</span>
            <span style={{ color: "#1976d2", cursor: "pointer", fontWeight: "bold" }}>Profile</span>
          </div>
        </div>
      </div>

      {/* KPIs */}
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "1rem", gap: "1rem" }}>
        {[
          { label: "üí∞ Cost Savings", value: "$1.2M" },
          { label: "‚ôªÔ∏è Waste Saved", value: "210 kg" },
          { label: "‚úàÔ∏è Routes Analyzed", value: "350" },
          { label: "üåç CO‚ÇÇ Saved", value: "28 tonnes" },
        ].map((kpi, index) => (
          <div
            key={index}
            style={{
              flex: 1,
              background: "linear-gradient(to right, #d4fdd3, #e5f4fc)",
              padding: "1rem",
              borderRadius: "12px",
              boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
              textAlign: "center"
            }}
          >
            <div style={{ fontSize: "1.2rem", fontWeight: "bold" }}>{kpi.label}</div>
            <div style={{ fontSize: "1.6rem", fontWeight: "bold", marginTop: "0.5rem" }}>{kpi.value}</div>
          </div>
        ))}
      </div>

      {/* Quarterly Chart */}
      {showInsightChart && selectedRoute && (
        <div style={{ marginTop: "1.5rem", background: "#f9f9f9", padding: "1rem", borderRadius: "12px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            <h3 style={{ color: "#1976d2" }}>Quarterly Waste & Savings for {selectedRoute}</h3>
            <button
              onClick={handleClearChart}
              style={{ backgroundColor: "#e53935", color: "#fff", padding: "0.3rem 0.7rem", border: "none", borderRadius: "5px" }}
            >
              ‚ùå Clear
            </button>
          </div>
          <RouteInsightBox route={selectedRoute} />
        </div>
      )}

      {/* Pie Charts + Route Line Chart */}
      <div style={{ display: "flex", gap: "1rem", marginTop: "2rem" }}>
        <div style={{ flex: 1, background: "#f7f7f7", padding: "1rem", borderRadius: "12px" }}>
          <h3 style={{ textAlign: "center" }}>Consumption Breakdown</h3>
          <div style={{ display: "flex", justifyContent: "space-around", alignItems: "center" }}>
            <div style={{ textAlign: "center" }}>
              <h4>Food</h4>
              <PieChartFood />
            </div>
            <div style={{ textAlign: "center" }}>
              <h4>Beverages</h4>
              <PieChartBeverage />
            </div>
          </div>
        </div>

        <div style={{ flex: 1 }}>
          <RouteLineChart />
        </div>
      </div>

      {/* Route Filter Chart */}
      <div style={{ marginTop: "2rem" }}>
        <RouteFilter />
      </div>

      {/* Age Group Section */}
      <div style={{ marginTop: "2rem" }}>
        <h3>Waste by Age Group</h3>
        <BarChartComponent />
        <div>
          <AgeGroupDetails />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
