import React from "react";
import PieChartComponent from "./PieChartComponent";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import AgeGroupDetails from "./AgeGroupDetails";

function Dashboard() {
  return (
    <div style={{ padding: "2rem" }}>
      {/* üîπ Top Navbar */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1rem" }}>
        <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", color: "#004e92" }}>‚úàÔ∏è In-Flight Insights Dashboard</h1>
        <div style={{ display: "flex", gap: "2rem" }}>
          <a href="/dashboard" style={navStyle}>Dashboard</a>
          <a href="/sop" style={navStyle}>Reports</a>
          <a href="/sop" style={navStyle}>Savings</a>
        </div>
      </div>

      {/* üîπ Key Indicators */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          gap: "2rem",
          backgroundColor: "#f8f9fa",
          padding: "1.5rem 2rem",
          margin: "1.5rem 0",
          borderRadius: "10px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        }}
      >
        <KPIBox title="üí∞ Cost Savings" value="$1.2M" />
        <KPIBox title="‚ôªÔ∏è Waste Saved" value="210 kg" />
        <KPIBox title="üõ´ Routes Analyzed" value="350" />
      </div>

      {/* üîπ Pie Chart & Route Line Chart Side by Side */}
      <div style={{ display: "flex", gap: "2rem", marginBottom: "2rem" }}>
        <div style={{ flex: 1 }}>
          <h2>Overall Food Consumption</h2>
          <PieChartComponent />
        </div>
        <div style={{ flex: 1 }}>
          <h2>Route-Specific Waste Trend</h2>
          <RouteLineChart />
        </div>
      </div>

      {/* üîπ Bar Chart */}
      <div>
        <h2>Waste by Age Group</h2>
        <BarChartComponent />
      </div>

      {/* üîπ Age Group Details */}
      <div style={{ marginTop: "2rem" }}>
        <h2>Age Group Details</h2>
        <p>[Details will be shown here on click of bar chart]</p>
        <AgeGroupDetails />
      </div>
    </div>
  );
}

// üî∏ Reusable KPI Component
const KPIBox = ({ title, value }) => (
  <div
    style={{
      flex: 1,
      backgroundColor: "#fff",
      padding: "1rem",
      borderRadius: "8px",
      textAlign: "center",
      boxShadow: "0 1px 4px rgba(0,0,0,0.05)",
    }}
  >
    <h4 style={{ fontSize: "1.5rem", color: "#555", marginBottom: "0.5rem" }}>{title}</h4>
    <p style={{ fontSize: "2.0rem", fontWeight: "bold", margin: 0 }}>{value}</p>
  </div>
);

// üî∏ Navbar Link Style
const navStyle = {
  textDecoration: "none",
  color: "#004e92",
  fontWeight: "bold",
  fontSize: "1rem"
};

export default Dashboard;
