import React, { useState } from "react";
import inflightLogo from "../assets/inflight-logo.png";
import PieChartFood from "./PieChartFood";
import PieChartBeverage from "./PieChartBeverage";
import BarChartComponent from "./BarChartComponent";
import RouteLineChart from "./RouteLineChart";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  LineChart,
  Line,
  CartesianGrid,
  Legend,
} from "recharts";

const routeOptions = ["NYC-LAX", "ATL-PHX", "SEA-JFK"];

const untouchedItemsData = {
  "NYC-LAX": [
    { name: "Salad", count: 30 },
    { name: "Bread Roll", count: 20 },
    { name: "Fruit Cup", count: 18 },
  ],
  "ATL-PHX": [
    { name: "Yogurt", count: 25 },
    { name: "Granola Bar", count: 15 },
    { name: "Egg Wrap", count: 12 },
  ],
  "SEA-JFK": [
    { name: "Pasta", count: 22 },
    { name: "Biscuit", count: 16 },
    { name: "Juice Box", count: 14 },
  ],
};

const costSavingsData = {
  "NYC-LAX": [
    { month: "Jan", savings: 5000 },
    { month: "Feb", savings: 5800 },
    { month: "Mar", savings: 6400 },
    { month: "Apr", savings: 7200 },
  ],
  "ATL-PHX": [
    { month: "Jan", savings: 4200 },
    { month: "Feb", savings: 5000 },
    { month: "Mar", savings: 5300 },
    { month: "Apr", savings: 6100 },
  ],
  "SEA-JFK": [
    { month: "Jan", savings: 3100 },
    { month: "Feb", savings: 4000 },
    { month: "Mar", savings: 4600 },
    { month: "Apr", savings: 5200 },
  ],
};

const Dashboard = () => {
  const [selectedRoute, setSelectedRoute] = useState("");
  const [showGraphs, setShowGraphs] = useState(false);

  const handleFilter = () => {
    if (selectedRoute) {
      setShowGraphs(true);
    }
  };

  return (
    <div style={{ padding: "2rem", backgroundColor: "white" }}>
      {/* Header Section */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "1.5rem",
        }}
      >
        {/* Logo + Title */}
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={inflightLogo}
            alt="In-Flight Insights Logo"
            style={{
              height: "120px",
              width: "120px",
              objectFit: "contain",
              backgroundColor: "transparent",
              marginRight: "1rem",
            }}
          />
          <h1 style={{ fontSize: "2.4rem", color: "#2c65c8", margin: 0 }}>
            Sustainability Analytics
          </h1>
        </div>

        {/* Top-Right Navigation */}
        <div style={{ display: "flex", gap: "1.5rem", fontWeight: "bold", fontSize: "1rem" }}>
          <a href="#dashboard" style={navLinkStyle}>Dashboard</a>
          <a href="#reports" style={navLinkStyle}>Reports</a>
          <a href="#settings" style={navLinkStyle}>Settings</a>
          <a href="#profile" style={navLinkStyle}>Profile</a>
        </div>
      </div>

      {/* KPI Cards */}
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "2rem", flexWrap: "wrap", gap: "1rem" }}>
        {[
          { title: "ðŸ’° Cost Savings", value: "$1.2M" },
          { title: "â™»ï¸ Waste Saved", value: "210 kg" },
          { title: "ðŸ›« Routes Analyzed", value: "350" },
          { title: "ðŸŒ COâ‚‚ Saved", value: "28 tonnes" },
        ].map((item, idx) => (
          <div
            key={idx}
            style={{
              flex: "1",
              minWidth: "200px",
              background: "linear-gradient(to right, #e0f7e9, #e6f2fb)",
              padding: "1rem",
              borderRadius: "12px",
              textAlign: "center",
              boxShadow: "0 0 6px rgba(0,0,0,0.1)",
            }}
          >
            <h3 style={{ fontSize: "1.2rem", margin: 0 }}>{item.title}</h3>
            <p style={{ fontSize: "1.8rem", fontWeight: "bold", marginTop: "0.5rem" }}>{item.value}</p>
          </div>
        ))}
      </div>

      {/* Pie Charts + Route Line Chart */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "1.5rem", marginBottom: "2rem" }}>
        {/* Pie Charts */}
        <div style={{ flex: 1, background: "#f9f9f9", padding: "1.5rem", borderRadius: "12px" }}>
          <h2 style={{ textAlign: "center" }}>Consumption Breakdown</h2>
          <div style={{ display: "flex", justifyContent: "space-around" }}>
            <div>
              <h4>Food</h4>
              <PieChartFood />
            </div>
            <div>
              <h4>Beverages</h4>
              <PieChartBeverage />
            </div>
          </div>
        </div>

        {/* Route-Specific Waste Trend */}
        <div style={{ flex: 1, background: "#f9f9f9", padding: "1.5rem", borderRadius: "12px" }}>
          <h2 style={{ textAlign: "center" }}>Route-Specific Waste Trend</h2>
          <RouteLineChart />
        </div>
      </div>

      {/* Route Filter Feature */}
      <div style={{ marginBottom: "2rem", textAlign: "center" }}>
        <select
          value={selectedRoute}
          onChange={(e) => setSelectedRoute(e.target.value)}
          style={{ padding: "10px", fontSize: "1rem", marginRight: "1rem", borderRadius: "6px" }}
        >
          <option value="">Select Flight Route</option>
          {routeOptions.map((route) => (
            <option key={route} value={route}>
              {route}
            </option>
          ))}
        </select>
        <button
          onClick={handleFilter}
          style={{
            padding: "10px 18px",
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            fontSize: "1rem",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          Filter by Flight Route
        </button>
      </div>

      {/* Filtered Route Graphs */}
      {showGraphs && selectedRoute && (
        <div style={{ display: "flex", gap: "2rem", flexWrap: "wrap", justifyContent: "center" }}>
          {/* Untouched Items */}
          <div style={{ background: "#f4f4f4", padding: "1rem", borderRadius: "10px" }}>
            <h3>Untouched/Substitutable Items for {selectedRoute}</h3>
            <ResponsiveContainer width={300} height={250}>
              <BarChart data={untouchedItemsData[selectedRoute]}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="count" fill="#ff7f50" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          {/* Cost Savings */}
          <div style={{ background: "#f4f4f4", padding: "1rem", borderRadius: "10px" }}>
            <h3>Cost Savings Over Time ({selectedRoute})</h3>
            <ResponsiveContainer width={350} height={250}>
              <LineChart data={costSavingsData[selectedRoute]}>
                <CartesianGrid stroke="#ccc" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="savings" stroke="#0088FE" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      )}

      {/* Bar Chart for Age */}
      <div>
        <h2 style={{ fontSize: "1.2rem", marginTop: "2rem" }}>Waste by Age Group</h2>
        <BarChartComponent />
      </div>
    </div>
  );
};

